insert  into `cms_whoweare_text`(`whowearetext_whoweare_id`,`whowearetext_lang`,`whowearetext_text`) values (1,'en','<p xss=\"removed\"><b>PT. BHUMI VISATANDA</b></p><p xss=\"removed\"><b><br></b><span style=\"font-size: 1rem;\">PT Bhumi Visatanda atau dikenal sebagai BHIVA merupakan anak perusahaan PT Taman Wisata Candi Borobudur, Prambanan &amp; Ratu Boko (Persero) yang berdiri sejak tahun 1996.&nbsp; Berpusat di kota pariwisata Yogyakarta, nama perusahaan ini diambil dari bahasa Sansekerta yang berarti Kunjungi Bumi Kita Sendiri.&nbsp;</span></p><p xss=\"removed\">Dengan layanan global yang berpusat dari kota budaya Yogyakarta, Indonesia, BHIVA memiliki empat aktifitas utama yaitu: MICE &amp; Event, Tour &amp; Travel, Transportasi, &amp; Reservasi.</p><p xss=\"removed\"><span style=\"font-size: 1rem;\">Berbagai perusahaan nasional dan multinasional telah mempercayakan eventnya pada BHIVA, seperti, Kementerian Keuangan, Kemeng BUMN, Kantor Kedutaan, Angkasa Pura, Gramedia, UGM, Atmajaya, UII, Yayasan Santa Maria, PT Telekomunikasi Indonesia, Bank Indonesia hingga Coca Cola, JP Morgan dan lain sebagainya.</span><br></p>'),(1,'id','<p xss=\"removed\"><span style=\"font-weight: bolder;\">PT. BHUMI VISATANDA</span></p><p xss=\"removed\"><span style=\"font-weight: bolder;\"><br></span><br></p><p xss=\"removed\">PT Bhumi Visatanda atau dikenal sebagai BHIVA merupakan anak perusahaan PT Taman Wisata Candi Borobudur, Prambanan &amp; Ratu Boko (Persero) yang berdiri sejak tahun 1996.&nbsp; Berpusat di kota pariwisata Yogyakarta, nama perusahaan ini diambil dari bahasa Sansekerta yang berarti Kunjungi Bumi Kita Sendiri.&nbsp;</p><p xss=\"removed\">Dengan layanan global yang berpusat dari kota budaya Yogyakarta, Indonesia, BHIVA memiliki empat aktifitas utama yaitu: MICE &amp; Event, Tour &amp; Travel, Transportasi, &amp; Reservasi.</p><p xss=\"removed\"><span style=\"font-size: 1rem;\">Berbagai perusahaan nasional dan multinasional telah mempercayakan eventnya pada BHIVA, seperti, Kementerian Keuangan, Kemeng BUMN, Kantor Kedutaan, Angkasa Pura, Gramedia, UGM, Atmajaya, UII, Yayasan Santa Maria, PT Telekomunikasi Indonesia, Bank Indonesia hingga Coca Cola, JP Morgan dan lain sebagainya.</span><br></p>');

